import React, { useState } from 'react'
import { StyleSheet, Text, View, Image, TouchableOpacity, Dimensions } from 'react-native';
import { WebView } from 'react-native-webview'
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';

const Stack = createStackNavigator();


function HomeScreen({ navigation }) {

  return (
    <View style={styles.container}>
      <View style={{width: '80%', aspectRatio: 1}}>
        <View style={styles.imageView}>
          <Image source={require('./assets/slackProfilePicture.png')} style={styles.profilePics} />
        </View>
        <Text style={styles.slackName}>AjKenz</Text>
      </View>
      <TouchableOpacity onPress={() => navigation.navigate('Web')} style={styles.button}>
        <Text style={styles.buttonText}>Open GitHub</Text>
      </TouchableOpacity>
    </View>
  );
}

function WebScreen() {
  const gitUrl = 'https://github.com/AjKenz'
  return (
    <View style={styles.container}>
      <WebView source={{ uri: gitUrl }} style={styles.webViewStyle} />
    </View>
  )
}

const styles = StyleSheet.create({
  container: {
    backgroundColor: '#fff0',
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center'
  },
  imageView: {
    borderRadius: 10,
    elevation: 5,
    shadowColor: 'black',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.4,
    shadowRadius: 3,
    aspectRatio: 1
  },
  profilePics: {
    width: '100%', // You can adjust the percentage as needed
    aspectRatio: 1, // Maintain a 1:1 aspect ratio for a square image
    resizeMode: 'cover',
  },
  slackName: {
    fontWeight: 'bold',
    fontSize: 30,
    color: '#000',
    elevation: 3,
    shadowColor: 'black',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.4,
    shadowRadius: 3,

  },
  button: {
    width: '30%',
    height: 50,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
    borderRadius: 50,
    marginVertical: 50,
    borderWidth: 2,
    elevation: 5,
    shadowColor: 'black',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.4,
    shadowRadius: 3,
  },
  buttonText: {
    color: '#000',
    fontWeight: 'bold'
  },
  webViewStyle: {
    width: Dimensions.get('window').width
  }
});

export default function App() {
  return (
    <NavigationContainer>
      <Stack.Navigator screenOptions={{
        headerStyle: {
          backgroundColor: '#1F2328',
        },
        headerTintColor: 'white',
        headerTitleStyle: {
          fontWeight: 'bold',
          fontSize: 20
        },
      }}>
        <Stack.Screen name="Home" component={HomeScreen} options={{ title: 'Slack Identity' }} />
        <Stack.Screen name="Web" component={WebScreen} options={{ title: 'Git Profile', headerBackTitleVisible: false }} />
      </Stack.Navigator>
    </NavigationContainer>
  );
}
